def max_area(grid):
    max_area = 0
    explored = set()
    islands = find_island_pieces(grid)
    flag = False
    for row in grid:
        for cell in row:
            if cell != 1:
                flag = True
    if not flag:
        return len(grid) * len(grid[0])
            
    for island in islands:
        if island not in explored:
            area,found = calculate_area(grid, island[0], island[1])
            for isle in found:
                explored.add(isle)
            if area > max_area:
                max_area = area
            explored.extend(found)
    return max_area

def calculate_area(grid, r, c):
    explored = set()
    unexplored = [(r,c)]
    while len(unexplored) > 0:
        island = unexplored.pop(0)
        explored.add(island)
        r = island[0]
        c = island[1]
        r_max = len(grid)
        c_max = len(grid[0])
        if c+1 < c_max:
            if grid[r][c+1] == 1:
                if (r,c+1) not in explored:
                    unexplored.append((r,c+1))
        if c-1 >= 0:
            if grid[r][c-1] == 1:
                if (r,c-1) not in explored:
                    unexplored.append((r,c-1))
        if r+1 < r_max:
            if grid[r+1][c] == 1:
                if (r+1,c) not in explored:
                    unexplored.append((r+1,c))
        if r-1 >= 0:
            if grid[r-1][c] == 1:
                if(r-1,c) not in explored:
                    unexplored.append((r-1,c))
    return len(explored),explored

def find_island_pieces(grid):
    islands = []
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            if grid[i][j] == 1:
                islands.append((i,j))
    return islands

